import React, { useState, useEffect } from 'react';
import styles from './CSS_modules/JobFeed.module.css';
import axios from 'axios';
import moment from 'moment';

// GET API = `https://hacker-news.firebaseio.com/v0/jobstories.json`;
// Metadata GET API = https://hacker-news.firebaseio.com/v0/item/YOUR_POST_ID_HERE.json`

const FetchIds = async() => {
    const [IDs, setIDs] = useState([])

    // useEffect (no dependencies) 
    //pulls data from API using .get function, can see data or error in console (dev tools)
    //if get is successful, IDs is set to response.data (the IDs)
    useEffect(() => {
      axios.get(`https://hacker-news.firebaseio.com/v0/jobstories.json`)
        .then(response =>{
          console.log(response)
          setIDs(response.data)
        })
        .catch(error => {
          console.log(error)
        })
    }, [])

    //return unordered list of IDs
    return (  
      IDs
      // <ol style={{ fontWeight: '200', textAlign: 'left' }}>
      //   {
      //     IDs.map(((id) => <li>{id}</li>))
      //   }
      // </ol>
  )
}

function Page({ allIds }) {
  const [pageNumber, setPageNumber] = useState(0);
  if (pageNumber === 13){
    setPageNumber(0)
  }
  return (
    <button value={pageNumber} onClick={e => {
      setPageNumber(pageNumber+1);
    }}> Page Number: {pageNumber} </button>
  )
}

function JobFeed() {
  const [posts, setPosts] = useState([]);
  const [perPage, setPerPage] = useState(5);
  const [jobIds, setJobIds] = useState([]);
  const [pageNumber, setPageNumber] = useState(0)

  //Call fetch IDs whenever page number changes (i.e., user hits 'load more' button)
  useEffect(() => {
    FetchIds();
  }, [pageNumber]);

  const FetchPosts = (postIds) => {
    const [posts, setPosts] = useState('')

    postIds.forEach((postId) => {
      axios.get(`https://hacker-news.firebaseio.com/v0/item/${postId}.json`)
        .then(response =>{
          console.log(response)
          setPosts((posts) => [...posts, response.data])
        })
        .catch(error => {
          console.log(error)
        })
    })
  }

  //get all job IDS, put in array

  //get metadata for all job IDs, put in array

  //add button to choose page

  //display 5 posts per page
    //select page 
    //iterate through index+(5*page num) plus 5


  return (
    <div className={styles.container}>
      <h1 className={styles.header}>Hackernews Jobs</h1>
      {<Page/>}
      <ol style={{ fontWeight: '200', textAlign: 'left' }}>

      </ol>
    </div>
  );
};

export default JobFeed;


// Job Post Object
const Post = ({ post }) => {
  return (
    <a href={post?.url} target="__blank" style={{ textDecoration: 'none' }}>
      <div className={styles.postContainer}>
        <p>
          ID: <span>{post?.id}</span>{' '}
        </p>
        <h1>{post?.title}</h1>
        <p>{moment(post?.time).format('Do MMM YYYY hh:mm a')}</p>
        <p>
          Posted by: <span>{post?.by}</span>{' '}
        </p>
      </div>
    </a>
  );
};

