import React, { useState, useEffect } from 'react';
import styles from './CSS_modules/JobFeed.module.css';
import axios from 'axios';

// GET API = `https://hacker-news.firebaseio.com/v0/jobstories.json`;
// Metadata GET API = https://hacker-news.firebaseio.com/v0/item/YOUR_POST_ID_HERE.json`

function FetchIds() {
    const [IDs, setIDs] = useState([])

    // useEffect (no dependencies) 
    //pulls data from API using .get function, can see data or error in console (dev tools)
    //if get is successful, IDs is set to response.data (the IDs)
    useEffect(() => {
      axios.get(`https://hacker-news.firebaseio.com/v0/jobstories.json`)
        .then(response =>{
          console.log(response)
          setIDs(response.data)
        })
        .catch(error => {
          console.log(error)
        })
    }, [])

    //return unordered list of IDs
    return (      
      <ul style={{ fontWeight: '200', textAlign: 'center' }}>
        {
          IDs.map(((id) => <li>{id}</li>))
        }
      </ul>
  )
}


function JobFeed() {
  const HACKERNEWS_POSTID_API = `https://hacker-news.firebaseio.com/v0/jobstories.json`;
  const [posts, setPosts] = useState([]);
  const [pageNumber, setPageNumber] = useState(0);
  const [perPage, setPerPage] = useState(5);
  const [jobIds, setJobIds] = useState([]);
  const [endReached, setEndReached] = useState(false);

  useEffect(() => {
    FetchIds();
  }, [pageNumber]);

  return (
    <div className={styles.container}>
      <h1 className={styles.header}>Hackernews Jobs</h1>
      {<FetchIDs/>}
    </div>
  );
};

export default JobFeed;
